2 Algorithm
def parabola_search(f, x1, x2, x3, epsilon):
    f1, f2, f3 = f(x1), f(x2), f(x3)
    while x3 - x1 &gt; epsilon:
        u = x2 - ((x2 - x1)**2*(f2 - f3) - (x2 - x3)**2*(f2 - f1))/(2*((x2 - x1)*(f2 - f3) - (x2 - x3)*(f2 - f1)))
        fu = f(u)

        if x2 &lt;= u:
            if f2 &lt;= fu:
                x1, x2, x3 = x1, x2, u
                f1, f2, f3 = f1, f2, fu
            else:
                x1, x2, x3 = x2, u, x3
                f1, f2, f3 = f2, fu, f3
        else:
            if fu &lt;= f2:
                x1, x2, x3 = x1, u, x2
                f1, f2, f3 = f1, fu, f2
            else:
                x1, x2, x3 = u, x2, x3
                f1, f2, f3 = fu, f2, f3
    return (x1 + x3) / 2

2 Algorithm
def binary_search(f, a, b, epsilon):
    c = (a + b) / 2
    while abs(b - a) &gt; epsilon:
        y = (a + c) / 2.0
        if f(y) &lt;= f(c):
            b = c
            c = y
        else:
            z = (b + c) / 2.0
            if f(c) &lt;= f(z):
                a = y
                b = z
            else:
                a = c
                c = z
    return c



